import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { fetchDoctorTimeslots } from '../../util/fetch';

const BookAppointment = ({ doctor }) => {
  const [modalIsOpen, setModalIsOpen] = useState(true);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [timeSlots, setTimeSlots] = useState([]); // State to store fetched timeslots
  const [timeSlot, setTimeSlot] = useState('');
  const [medicalHistory, setMedicalHistory] = useState('');
  const [symptoms, setSymptoms] = useState('');


  const handleCloseModal = () => setModalIsOpen(false);
  const isTimeSlotSelected = timeSlot !== '';
  
  // Fetch available timeslots on modal open
  useEffect(() => {
    const fetchData = async () => {
      const fetchedTimeSlots = await fetchDoctorTimeslots(doctor.doctorId, selectedDate);
      setTimeSlots(fetchedTimeSlots);
    };
    if (modalIsOpen) {
      fetchData();
    }
  }, [modalIsOpen, doctor.doctorId, selectedDate, fetchDoctorTimeslots]);

  return (
    <>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={handleCloseModal}
        style={{ content: { margin: 'auto' } }}
        className="bookAppointmentModal" // Use classes for custom styling (optional)
      >
        <div className="bookAppointmentMainCard">
          <h2>Book an Appointment</h2>
          <TextField
            label="Doctor Name"
            value={doctor.firstName + " " + doctor.lastName}
            disabled
          />
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              disableToolbar
              variant="inline"
              format="MM/dd/yyyy"
              margin="normal"
              label="Appointment Date"
              value={selectedDate}
              onChange={(e) => setSelectedDate(date)}
              KeyboardButtonProps={{
                disabled: true, // Disable keyboard input for date selection
              }}
            />
          </MuiPickersUtilsProvider>
          <FormControl >
            <Select
              value={timeSlot}
              onChange={(e) => setTimeSlot(e.target.value)}
              displayEmpty
              error={!isTimeSlotSelected}
            >
              <MenuItem value="">Select a time slot</MenuItem>
              {timeSlots.map((slot) => (
                <MenuItem key={slot} value={slot}>
                  {slot}
                </MenuItem>
              ))}
            </Select>
            {!isTimeSlotSelected && <span className="errorcontainer">Select a time slot</span>}
          </FormControl>
          <FormControl >
            <TextField
              label="Medical History"
              multiline
              value={medicalHistory}
              onChange={(e) => setMedicalHistory(e.target.value)}
              variant="outlined"
            />
          </FormControl>
          <FormControl >
            <TextField
              label="Symptoms"
              multiline
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
              variant="outlined"
            />
          </FormControl>
          <Button variant="contained" color="primary" size="small">BOOK APPOINTMENT</Button>
        </div>
      </Modal>
    </>
  );
};

export default BookAppointment;
