import React, { useState } from 'react';
import './Header.css'
import Button from '@material-ui/core/Button';
import Modal from 'react-modal';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Login from '../../screens/login/Login';
import logo from '../../assets/logo.jpeg'
import Register from '../../screens/register/Register';

const Header = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [activeTab, setActiveTab] = useState(0);

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const handleChangeTab = (event, newValue) => {
        setActiveTab(newValue);
    };

    return (
        <>
            <div className="header">
                <img className='logo' src={logo} />
                <div className='title'>Doctor Finder</div>
                {localStorage.getItem('isLoggedIn') ? (
                    <Button variant="contained" color="secondary" onClick={() => { /* handle logout */ }}>
                        Logout
                    </Button>
                ) : (
                    <Button variant="contained" color="primary" onClick={handleOpenModal}>
                        Login
                    </Button>
                )}
            </div>
            <div >
            <Modal isOpen={isModalOpen} contentLabel="Login/Register" onRequestClose={handleCloseModal} >
                <Card >
                    <CardContent>
                        <Tabs  value={activeTab} onChange={handleChangeTab}>
                            <Tab label="LOGIN" />
                            <Tab label="REGISTER" />
                        </Tabs>
                        {activeTab === 0 && ( // Show Login form if activeTab is 0 (LOGIN)
                            <Login/>
                        )}
                        {activeTab === 1 && ( // Show Register form if activeTab is 1 (REGISTER)
                            <Register/>
                        )}
                    </CardContent>
                </Card>
            </Modal>
            </div>
            
        </>
    );
};

export default Header;
