import React, { useState, useNavigate } from 'react';
import { FormControl, InputLabel, Input, Button } from '@material-ui/core';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [emailError, setEmailError] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    //const navigate = useNavigate();
    const handleChange = (event) => {
        const { name, value } = event.target;
        if (name === 'email') {
            setEmail(value);
            setEmailError(false); // Reset email error on change
        } else if (name === 'password') {
            setPassword(value);
        }
    };
    const RedirectToHome = () => {
        const navigate = useNavigate();
        useEffect(() => {
          navigate('/'); // Navigate on component mount
        }, [navigate]);
      
        return null; // No need to render anything here
      };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage(''); // Clear any previous error message

        if (!email || !password) {
            setErrorMessage('Please fill out all required fields');
            return;
        }

        const validEmail = /\S+@\S+\.\S+/.test(email);
        if (!validEmail) {
            setEmailError(true);
            return;
        }

        try {
            const encoded = Buffer.from(email + ':' + password).toString('base64');
            const response = await axios.post("http://localhost:8080/auth/login", {}, {
                headers: {
                    'Authorization': 'Basic ' + encoded
                }
            });
            // Handle successful login (e.g., close modal, redirect, etc.)
            localStorage.setItem('token', response.data.accessToken);
            console.log('Login successful:', localStorage.getItem('token'));
            setIsLoggedIn(true);
          //  navigate('/');
         if(isLoggedIn) {
            RedirectToHome();
         }

        } catch (error) {
            console.error('Login error:', error);
            // Handle login errors (e.g., display error message)
            setErrorMessage('Invalid email or password');
        }
    };

    return (
        <form onSubmit={handleSubmit} autoComplete="off" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <FormControl margin="normal" required error={emailError}>
                <InputLabel htmlFor="email">Email Address</InputLabel>
                <Input
                    id="email"
                    name="email"
                    autoComplete="email"
                    autoFocus
                    value={email}
                    onChange={handleChange}
                />
                {emailError && <span style={{ color: 'red' }}>Enter valid Email</span>}
            </FormControl>
            <FormControl margin="normal" required>
                <InputLabel htmlFor="password">Password</InputLabel>
                <Input
                    id="password"
                    name="password"
                    type="password"
                    value={password}
                    onChange={handleChange}
                />
            </FormControl>
            <Button type="submit" variant="contained" color="primary">
                LOGIN
            </Button>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </form>
    );
};

export default Login;
