import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Card, CardContent, Button, Typography, FormControl, TextField, Select, MenuItem, InputLabel, Box } from '@mui/material';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import { fetchDoctorTimeslots } from '../../util/fetch';

const BookAppointment = ({ isOpen, onRequestClose, doctor }) => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [timeSlot, setTimeSlot] = useState('');
    const [medicalHistory, setMedicalHistory] = useState('');
    const [symptoms, setSymptoms] = useState('');
    const [timeslots, setTimeslots] = useState([]);
    const [timeSlotError, setTimeSlotError] = useState(false);

    useEffect(() => {
        async function getTimeslots() {
            try {
                const availableTimeslots = await fetchDoctorTimeslots(doctor.id, selectedDate);
                setTimeslots(availableTimeslots);
            } catch (error) {
                console.error('Error fetching timeslots:', error);
            }
        }

        getTimeslots();
    }, [doctor.id, selectedDate]);

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const handleTimeSlotChange = (event) => {
        setTimeSlot(event.target.value);
        setTimeSlotError(false);
    };

    const handleSubmit = () => {
        if (!timeSlot) {
            setTimeSlotError(true);
            return;
        }

        // Handle form submission logic here
        console.log({
            doctorName: `${doctor.firstName} ${doctor.lastName}`,
            selectedDate,
            timeSlot,
            medicalHistory,
            symptoms
        });

        onRequestClose(); // Close the modal after submission
    };

    return (
        <Modal isOpen={isOpen} onRequestClose={onRequestClose}>
            <Card>
                <CardContent>
                    <Typography variant="h5">Book an Appointment</Typography>
                    <Box component="form" sx={{ mt: 2 }}>
                        <FormControl fullWidth margin="normal">
                            <TextField
                                label="Doctor's Name"
                                value={`${doctor.firstName} ${doctor.lastName}`}
                                InputProps={{
                                    readOnly: true,
                                }}
                                variant="outlined"
                            />
                        </FormControl>
                        <FormControl fullWidth margin="normal">
                            <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                <KeyboardDatePicker
                                    margin="normal"
                                    id="date-picker-dialog"
                                    label="Appointment Date"
                                    format="MM/dd/yyyy"
                                    value={selectedDate}
                                    onChange={handleDateChange}
                                    KeyboardButtonProps={{
                                        'aria-label': 'change date',
                                    }}
                                />
                            </MuiPickersUtilsProvider>
                        </FormControl>
                        <FormControl fullWidth margin="normal" error={timeSlotError}>
                            <InputLabel id="time-slot-label">Time Slot</InputLabel>
                            <Select
                                labelId="time-slot-label"
                                id="time-slot"
                                value={timeSlot}
                                onChange={handleTimeSlotChange}
                                variant="outlined"
                            >
                                <MenuItem value="">
                                    <em>Select a time slot</em>
                                </MenuItem>
                                {timeslots.map((slot) => (
                                    <MenuItem key={slot} value={slot}>{slot}</MenuItem>
                                ))}
                            </Select>
                            {timeSlotError && <Typography color="error">Select a time slot</Typography>}
                        </FormControl>
                        <FormControl fullWidth margin="normal">
                            <TextField
                                label="Medical History"
                                value={medicalHistory}
                                onChange={(e) => setMedicalHistory(e.target.value)}
                                variant="outlined"
                                multiline
                                rows={4}
                            />
                        </FormControl>
                        <FormControl fullWidth margin="normal">
                            <TextField
                                label="Symptoms"
                                value={symptoms}
                                onChange={(e) => setSymptoms(e.target.value)}
                                variant="outlined"
                                multiline
                                rows={4}
                            />
                        </FormControl>
                        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                            <Button variant="contained" color="primary" onClick={handleSubmit}>
                                Book Appointment
                            </Button>
                        </Box>
                    </Box>
                </CardContent>
            </Card>
        </Modal>
    );
};

export default BookAppointment;
